sys_script.script:
(function executeRule(current, previous /*null when async*/) {
var token = gs.getProperty('x_1539348_so.scim.api.token');
    if (!token) {
        gs.error('Authorization token is missing. Set it in System Properties.');
        return;
    } else {
        gs.info('Token acquired: ' + token);
    }

    var username = current.username || '';
    var displayName = current.displayname || '';
    var email = current.email || '';
    var userType = current.usertype || '';

    var multiSelectField = current.roles || '';
    var selectedItems = multiSelectField ? multiSelectField.split(',') : [];

    // Fetch human-readable values
    var humanReadableItems = [];
    var gr = new GlideRecord('x_1539348_so_roles'); // Replace with your table name
    gr.addQuery('sys_id', 'IN', selectedItems.join(',')); // Match the UUIDs
    gr.query();
    while (gr.next()) {
        humanReadableItems.push(gr.value.toString());
    }

    // Log the results
    /*gs.info('Username: ' + username);
    gs.info('Display Name: ' + displayName);
    gs.info('Email: ' + email);
    gs.info('User Type: ' + userType);
    gs.info('Multi-Select Field: ' + multiSelectField);
    gs.info('Selected Items (UUIDs): ' + JSON.stringify(selectedItems));
    gs.info('Human-Readable Items (Droit): ' + JSON.stringify(humanReadableItems));*/

	var data = {
        "schemas": ["urn:ietf:params:scim:schemas:core:2.0:User"],
        "userName": username.toString(),
		"userType": userType.toString(),
        "displayName": displayName.toString(),
        "active": true,
        "emails": [
            {
                "value": email.toString(),
                "type": "work",
                "primary": true
            }
        ],
		"roles": humanReadableItems.map(function(role) {
            return { "value": role };
        })
    };

    // Log the final data object for debugging
    //gs.info('SCIM Data Object: ' + JSON.stringify(data));

    var jsonData = JSON.stringify(data);

    // Configurer la requête HTTP
    var request = new sn_ws.RESTMessageV2();
    request.setEndpoint('https://earth-apis.my.evidian.com/scim/v2/api/Users');
    request.setHttpMethod('POST');
    request.setRequestHeader('Content-Type', 'application/json');
    request.setRequestHeader('Authorization', 'Bearer ' + token);
    request.setRequestBody(jsonData);

    // Envoyer la requête
    try {
        var response = request.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

        // Log de la réponse pour le débogage
        gs.info('Statut HTTP: ' + httpStatus);
        gs.info('Corps de la réponse: ' + responseBody);

        // Gérer la réponse en fonction du code de statut
        if (httpStatus == 201 || httpStatus == 200) {
            gs.info('Données envoyées avec succès à l\'API SCIM.');
        } else {
            gs.error('Erreur lors de l\'envoi des données: ' + responseBody);
        }
    } catch(ex) {
        var message = ex.getMessage();
        gs.error('Exception lors de l\'appel API: ' + message);
    }
})(current, previous);
