sys_script_include.script:
var SCIMPatchUserRole = Class.create();
SCIMPatchUserRole.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    patchUserRole: function() {
        // Retrieve parameters sent from the client
        var userId = this.getParameter("sysparm_user_id");
        var roleId = this.getParameter("sysparm_role_id");

        try {

			var token = gs.getProperty('x_1539348_so.scim.api.token');
			gs.info(token);
            if (!token) {
                gs.error('API token is missing in system properties.');
            }

            // Construct the endpoint URL
            var baseUrl = "https://earth-apis.my.evidian.com/scim/v2/api/Users/";
            var endpoint = baseUrl + userId;

            // Create the payload
            var payload = JSON.stringify({
                schemas: ["urn:ietf:params:scim:api:messages:2.0:PatchOp"],
                Operations: [
                    {
                        op: "add",
                        path: "roles",
                        value: [
                            {
                                value: roleId
                            }
                        ]
                    }
                ]
            });

            // Create and configure the RESTMessageV2 request
            var request = new sn_ws.RESTMessageV2();
            request.setEndpoint(endpoint);
            request.setHttpMethod("PATCH");

            // Add necessary headers
            request.setRequestHeader("Content-Type", "application/json");
            request.setRequestHeader("Authorization", "Bearer " + token); // Replace with a valid token

            // Attach the request payload
            request.setRequestBody(payload);

            // Execute the HTTP request
            var response = request.execute();
            var httpStatus = response.getStatusCode();
            var responseBody = response.getBody();

            // Log and return the response
            gs.info("PATCH Request Status: " + httpStatus);
            gs.info("PATCH Response Body: " + responseBody);

            return responseBody;
        } catch (e) {
            gs.error("Error in SCIMPatchUserRole.patchUserRole: " + e.message);
            return JSON.stringify({ error: e.message });
        }
    },

    type: 'SCIMPatchUserRole'
});
