sys_script_include.script:
var SCIMCreatingUser = Class.create();
SCIMCreatingUser.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    // Method to handle the user creation via SCIM API
    createUser: function() {
        // Retrieve parameters passed from the UI Page
        var username = this.getParameter('username');
        var displayName = this.getParameter('displayName');
        var email = this.getParameter('email');
        var userType = this.getParameter('userType');
        var roles = this.getParameter('roles').split(',');  // Roles are passed as sys_ids

        gs.info('Received data from client: Username=' + username + ', Email=' + email + ', Roles=' + roles);

        // Ensure that the token exists
        var token = gs.getProperty('x_1539348_so.scim.api.token');
        if (!token) {
            return JSON.stringify({ success: false, message: 'Authorization token is missing. Set it in System Properties.' });
        }

        // Construct SCIM payload to create the user
        var data = {
            "schemas": ["urn:ietf:params:scim:schemas:core:2.0:User"],
            "userName": username,
            "userType": userType || '',
            "displayName": displayName || '',
            "active": true,
            "emails": [{
                "value": email,
                "type": "work",
                "primary": true
            }],
            "roles": roles.map(function(roleId) {
                return { "value": roleId }; // Use the sys_id directly in the SCIM payload
            })
        };

        // Prepare the SCIM API request
        var jsonData = JSON.stringify(data);
        var request = new sn_ws.RESTMessageV2();
        request.setEndpoint('https://earth-apis.my.evidian.com/scim/v2/api/Users'); // Replace with the correct SCIM endpoint
        request.setHttpMethod('POST');
        request.setRequestHeader('Content-Type', 'application/json');
        request.setRequestHeader('Authorization', 'Bearer ' + token);
        request.setRequestBody(jsonData);

        try {
            var response = request.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();

            gs.info('SCIM API Response: ' + responseBody); // Log SCIM API response

            // Return the response based on the status
            if (httpStatus == 201 || httpStatus == 200) {
                return JSON.stringify({ success: true, message: 'User created successfully.' });
            } else {
                return JSON.stringify({ success: false, message: 'Error during SCIM API request: ' + responseBody });
            }
        } catch (ex) {
            return JSON.stringify({ success: false, message: 'Exception: ' + ex.message });
        }
    },

    // Overriding the default get function to process AJAX requests
    getAjaxProcessor: function() {
        // Call createUser function when the AJAX call requests the method
        return this.createUser();
    }

});
