sys_ui_page.client_script:
function loadRolesForCheckboxes() {
    var ga = new GlideAjax('SCIMUserService');
    ga.addParam('sysparm_name', 'getRoles');

    ga.getXMLAnswer(function(response) {
        if (response) {
            try {
                // The response should be JSON string; ensure it's being parsed correctly
                var roles = JSON.parse(response);
                console.log('Available Roles:', roles);

                // Ensure the role-container is available
                var roleContainer = document.querySelector('.role-dropdown');
                if (!roleContainer) {
                    console.error('Role container not found!');
                    return;
                }

                // Clear existing roles before appending new ones
                roleContainer.innerHTML = '';

                // Append each role as a checkbox
                roles.forEach(function(role) {
                    var checkbox = document.createElement('input');
                    checkbox.type = 'checkbox';
                    checkbox.classList.add('role-option');
                    checkbox.id = 'role-' + role.value; // Make sure the role object has a 'value' property
                    checkbox.value = role.value;

                    var label = document.createElement('label');
                    label.setAttribute('for', 'role-' + role.value);
                    label.textContent = role.label; // Ensure the role object has a 'label' property

                    // Append the checkbox and label to the container
                    roleContainer.appendChild(checkbox);
                    roleContainer.appendChild(label);
                    roleContainer.appendChild(document.createElement('br')); // Adds a line break between checkboxes
                });
            } catch (e) {
                console.error('Error parsing roles response:', e);
            }
        } else {
            console.error('No response received from GlideAjax.');
        }
    });
}

// Ensure roles are loaded when the page is ready
document.addEventListener("DOMContentLoaded", function() {
    loadRolesForCheckboxes();
});

// Function to handle user creation via GlideAjax
function createUser() {
    // Handle required fields
    var requiredFields = document.querySelectorAll('.required');
    var allValid = true;

    requiredFields.forEach(function(field) {
        if (!field.value.trim()) {
            field.style.borderColor = 'red';  // Highlight empty fields
            allValid = false;
        } else {
            field.style.borderColor = '';  // Reset valid fields
        }
    });

    // Handle multiple selection for roles (checkboxes)
    var roleCheckboxes = document.querySelectorAll('.role-option:checked'); // Get all checked checkboxes
    if (roleCheckboxes.length === 0) {
        var roleContainer = document.querySelector('.role-dropdown');
        roleContainer.style.borderColor = 'red';  // Highlight if no roles are selected
        allValid = false;
    } else {
        var roleContainer = document.querySelector('.role-dropdown');
        roleContainer.style.borderColor = '';  // Reset if roles are selected
    }

    // If all fields are valid, proceed with creating the user
    if (allValid) {
        var username = document.getElementById('username').value;
        var displayName = document.getElementById('displayName').value;
        var email = document.getElementById('email').value;
        var userType = document.getElementById('userType').value;

        // Get selected roles from checkboxes
        var selectedRoles = Array.from(roleCheckboxes).map(function(checkbox) {
            return checkbox.value;
        });

        console.log('Creating User:', {
            username,
            displayName,
            email,
            userType,
            roles: selectedRoles
        });

        var ga = new GlideAjax('SCIMCreatingUser');
        ga.addParam('sysparm_name', 'createUser');
        ga.addParam('username', username);
        ga.addParam('displayName', displayName);
        ga.addParam('email', email);
        ga.addParam('userType', userType);
        ga.addParam('roles', selectedRoles.join(',')); // Pass selected roles (sys_id values)
        ga.getXMLAnswer(function(response) {
            var result = JSON.parse(response);
            if (result.success) {
                alert(result.message); // User created successfully
            } else {
                alert(result.message); // Error handling
            }
        });
    } else {
        alert('Please fill in all required fields and select at least one role.');
    }
}
